    static function getLevel ( $exp )
    {
        if( $exp == 0 )
            return 1;
        foreach( self::$_upgrade as $row ) {
            if( $row["needexp"] <= $exp ) { 
                if( $row["level"] < 130 ) 
                    continue;
                return 130;
            } 
            return $row["level"]-1; 
        } 
    }
    static function getUpgradeNeed( $exp )
    { 
        if( $exp == 0 )
            return self::$_upgrade[1];
        foreach( self::$_upgrade as $row ) {
            if( $row["needexp"] <= $exp ) { 
                if( $row["level"] < 130 ) 
                    continue;
                return self::$_upgrade[130] ;
            } 
            return self::$_upgrade[$row["id"]-1]; 
        } 
    }
